`,(0,n.jsx)(e.pre,{children:(0,n.jsx)(e.code,{className:"language-js",children:`const MAX_LOAD = 1000; // \u201C\u626B\u7801\u201D\u961F\u5217\uFF0C\u9650\u5236\u6700\u5927\u7B49\u5F85\u4EBA\u6570

class AuthManager {
  constructor() {
    // \u7528 Map \u6765\u6A21\u62DF\u961F\u5217
    this.data = new Map();
  }
  // \u83B7\u53D6\u5355\u4F8B
  static getManager() {
    if (!this.instance) {
      this.instance = new AuthManager();
    }
    return this.instance;
  }
  // \u4EE5 uid \u4E3A key\uFF0C\u4FDD\u5B58\u7528\u6237\u4FE1\u606F\u5728\u961F\u5217\u4E2D\uFF0C\u7565
  setAuth(uid, user) {}
  // \u4EE5 uid \u4E3A key \u83B7\u53D6\u7528\u6237\u4FE1\u606F\uFF0C\u7565
  getAuth(uid) {}
  // \u5224\u65AD\u961F\u5217\u662F\u5426\u5DF2\u6EE1\uFF0C\u6EE1\u5219\u5C1D\u8BD5\u6E05\u9664\u8FC7\u671F\u7684\u8BF7\u6C42
  isAvailable() {
    if (this.data.size >= MAX_LOAD) {
      [...this.data].forEach(([key, value]) => {
        if (Date.now() - value.createTime > 60 * 1000) {
          this.data.delete(key);
        }
      });
    }
    return this.data.size < MAX_LOAD;
  }
}
`})}),`
